type IWrap = 'row' | 'column' | 'row-reverse' | 'column-reverse';
type IJustifyContent =
  | 'flex-start'
  | 'flex-end'
  | 'center'
  | 'space-between'
  | 'space-around'
  | 'space-evenly'
  | undefined;

type IAlignContent =
  | 'flex-start'
  | 'flex-end'
  | 'center'
  | 'stretch'
  | 'space-between'
  | 'space-around'
  | undefined;

type ITextAlign = 'left' | 'right' | 'center';
type IFontWeight =
  | 'normal'
  | 'bold'
  | '100'
  | '200'
  | '300'
  | '400'
  | '500'
  | '600'
  | '700'
  | '800'
  | '900'
  | undefined;

type themeColor<T> = {
  light: T;
  dark: T;
};
